package ud3;
//mostrar los numeros del 1 al 100
public class ejercicio_basic_for {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 0;
		// i ++ NO ES i+1
		// i ++ ES i = i+1 y una asignacion
		for (i=1;i <= 100; i++) {
			System.out.println(i);
		}
		
	}

}
