package ud3;
//2.que muestre por pantalla los numeros del 1 al 100
public class hoja2_Ej2 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//declaracion de variables
		int i = 0;
		//Hacer un bucle mientras (i<=100)
		while (i < 100) {
			//incrementar i i<--i+1
			i = i+1 ;
			//mostrar i
			System.out.println(i);
			//fin_mientras
		}
	}
}
